import React from "react";
import Modal from 'react-modal';
Modal.setAppElement('#root'); 
const CustomModal = ({ isOpen, onClose, children }) => {
    return (<div
            onClick={onClose}
            style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                background: "rgba(0, 0, 0, 0.5)",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                overflow: "scroll"
            }}
        >
        <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      contentLabel="Ejemplo Modal"
    >

            <div
                style={{
                    background: "white",
                    height: "auto",
                    width: "90%",
                    margin: "auto",
                    padding: "5px 2% 2% 2%",
                    border: "2px solid #7fcacd",
                    borderRadius: "10px",
                    boxShadow: "2px solid black",
                }}
            >
                {children}
            </div>

        </Modal></div>
    );
};

export default Modal;
